import{createComponent,renderTemplate,maybeRenderHead,unescapeHTML}from"../../../../astro.mjs";import"kleur/colors";import"clsx";import"html-escaper";import"cssesc";const html='<h1 id="box-display-types-block-and-inline-boxes">Box Display Types (Block and Inline Boxes)<a class="anchor" href="#box-display-types-block-and-inline-boxes"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h1>\n<h2 id="inner-display-type">Inner display type<a class="anchor" href="#inner-display-type"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="outer-display-type">Outer display type<a class="anchor" href="#outer-display-type"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>📌 dictates how the box itself is displayed</li>\n</ul>\n<h3 id="inner-display-type-1">Inner display type<a class="anchor" href="#inner-display-type-1"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>📌 dictates how elements inside that box are laid out</li>\n</ul>\n<h2 id="outer-display-type-1">Outer display type<a class="anchor" href="#outer-display-type-1"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-block"><code>display: block;</code><a class="anchor" href="#display-block"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>The box will break onto a new line</li>\n<li>The <code>width</code> and <code>height</code> properties are respected</li>\n<li><code>padding</code>, <code>margin</code> and <code>border</code> will cause other elements to be pushed away from the box</li>\n<li>If <code>width</code> is not specified, the box will extend in the inline direction to fill the space available in its container.\n<ul>\n<li>In most cases, the box will become as wide as its container, filling up 100% of the space available.</li>\n</ul>\n</li>\n<li>📌<code>&#x3C;h1></code> and <code>&#x3C;p></code>, use <strong>block</strong> as their outer display type by default</li>\n</ul>\n<h3 id="display-inline"><code>display: inline;</code><a class="anchor" href="#display-inline"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>The box will not break onto a new line</li>\n<li>The <code>width</code> and <code>height</code> properties will not apply</li>\n<li>Top and bottom padding, margins, and borders will apply but will <strong>not cause</strong> other inline boxes to move away from the box</li>\n<li>Left and right padding, margins, and borders will apply and will <strong>cause</strong> other inline boxes to move away from the box.</li>\n<li><img src="https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/css-box-model-(盒模型)-inline.png" alt=""></li>\n<li>📌 <code>&#x3C;a></code>, <code>&#x3C;span></code>, <code>&#x3C;em></code> and <code>&#x3C;strong></code> use <strong>inline</strong> as their outer display type by default</li>\n</ul>\n<h3 id="display-inline-block"><code>display: inline-block</code><a class="anchor" href="#display-inline-block"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>provides a middle ground</li>\n<li>Use it if you do not want an item to break onto a new line, but do want it to respect width and height\n<ul>\n<li><img src="https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/css-box-model-(盒模型)-inline-block.png" alt=""></li>\n</ul>\n</li>\n<li>Allow padding to be set on it, making it easier for a user to click the link</li>\n</ul>\n<h2 id="️hiding-elements">🏷️<a href="Hiding-Elements">Hiding Elements</a><a class="anchor" href="#️hiding-elements"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-none"><code>display: none</code><a class="anchor" href="#display-none"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="️floats">🏷️<a href="Floats">Floats</a><a class="anchor" href="#️floats"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-flow-root"><code>display: flow-root</code><a class="anchor" href="#display-flow-root"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="️flexbox">🏷️<a href="Flexbox">Flexbox</a><a class="anchor" href="#️flexbox"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-flex"><code>display: flex</code><a class="anchor" href="#display-flex"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="inline-flex"><code>inline-flex</code><a class="anchor" href="#inline-flex"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="contents"><code>contents</code><a class="anchor" href="#contents"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>create a “phantom” container whose children act like direct children of the parent</li>\n</ul>\n<h2 id="️grids">🏷️<a href="Grids">Grids</a><a class="anchor" href="#️grids"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-grid"><code>display: grid</code><a class="anchor" href="#display-grid"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="inline-grid"><code>inline-grid</code><a class="anchor" href="#inline-grid"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="️table-style">🏷️<a href="Table-Style">Table Style</a><a class="anchor" href="#️table-style"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-table"><code>display: table</code><a class="anchor" href="#display-table"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="display-inline-table"><code>display: inline-table</code><a class="anchor" href="#display-inline-table"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table-caption"><code>table-caption</code><a class="anchor" href="#table-caption"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table-header-group"><code>table-header-group</code><a class="anchor" href="#table-header-group"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table-footer-group"><code>table-footer-group</code><a class="anchor" href="#table-footer-group"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table-row-group"><code>table-row-group</code><a class="anchor" href="#table-row-group"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table-column-group"><code>table-column-group</code><a class="anchor" href="#table-column-group"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table-row"><code>table-row</code><a class="anchor" href="#table-row"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table-column"><code>table-column</code><a class="anchor" href="#table-column"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table-cell"><code>table-cell</code><a class="anchor" href="#table-cell"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table"><a href="https://tailwindcss.com/docs/display#table">Table</a><a class="anchor" href="#table"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="️styling-lists">🏷️<a href="Styling-Lists">Styling Lists</a><a class="anchor" href="#️styling-lists"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-list-item"><code>display: list-item</code><a class="anchor" href="#display-list-item"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>',frontmatter={category:"Web Design",aliases:["Box Display Types (Block and Inline Boxes)"],draft:!1,title:"Box Display Types (Block and Inline Boxes)",type:"D",tags:["CSS"],DateStarted:"2024-01-09T00:00:00.000Z",DateModified:"2024-04-25T00:00:00.000Z",status:null,reviewed:1,Datereviewed:"2024-01-18T16:00:00.000Z",topic:"Box",difficulty:"Good",comment:"⭐⭐⭐","linter-yaml-title-alias":"Box Display Types (Block and Inline Boxes)",minutes:1,words:244},file="E:/SynologyDrive/Codespace/blog-site/mynote/my-fuwari-blog/src/content/posts/css/box-model-spacing/box-display-types-block-and-inline-boxes.md",url=void 0;function rawContent(){return"\n# Box Display Types (Block and Inline Boxes)\n\n## Inner display type\n\n### Outer display type\n\n- 📌 dictates how the box itself is displayed\n\n### Inner display type\n\n- 📌 dictates how elements inside that box are laid out\n\n## Outer display type\n\n### `display: block;`\n\n- The box will break onto a new line\n- The `width` and `height` properties are respected\n- `padding`, `margin` and `border` will cause other elements to be pushed away from the box\n- If `width` is not specified, the box will extend in the inline direction to fill the space available in its container.\n  - In most cases, the box will become as wide as its container, filling up 100% of the space available.\n- 📌`<h1>` and `<p>`, use **block** as their outer display type by default\n\n### `display: inline;`\n\n- The box will not break onto a new line\n- The `width` and `height` properties will not apply\n- Top and bottom padding, margins, and borders will apply but will **not cause** other inline boxes to move away from the box\n- Left and right padding, margins, and borders will apply and will **cause** other inline boxes to move away from the box.\n- ![](<https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/css-box-model-(盒模型)-inline.png>)\n- 📌 `<a>`, `<span>`, `<em>` and `<strong>` use **inline** as their outer display type by default\n\n### `display: inline-block`\n\n- provides a middle ground\n- Use it if you do not want an item to break onto a new line, but do want it to respect width and height\n  - ![](<https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/css-box-model-(盒模型)-inline-block.png>)\n- Allow padding to be set on it, making it easier for a user to click the link\n\n## 🏷️[Hiding Elements](Hiding-Elements)\n\n### `display: none`\n\n## 🏷️[Floats](Floats)\n\n### `display: flow-root`\n\n## 🏷️[Flexbox](Flexbox)\n\n### `display: flex`\n\n### `inline-flex`\n\n### `contents`\n\n- create a “phantom” container whose children act like direct children of the parent\n\n## 🏷️[Grids](Grids)\n\n### `display: grid`\n\n### `inline-grid`\n\n## 🏷️[Table Style](Table-Style)\n\n### `display: table`\n\n### `display: inline-table`\n\n### `table-caption`\n\n### `table-header-group`\n\n### `table-footer-group`\n\n### `table-row-group`\n\n### `table-column-group`\n\n### `table-row`\n\n### `table-column`\n\n### `table-cell`\n\n### [Table](https://tailwindcss.com/docs/display#table)\n\n## 🏷️[Styling Lists](Styling-Lists)\n\n### `display: list-item`\n"}function compiledContent(){return html}function getHeadings(){return[{depth:1,slug:"box-display-types-block-and-inline-boxes",text:"Box Display Types (Block and Inline Boxes)#"},{depth:2,slug:"inner-display-type",text:"Inner display type#"},{depth:3,slug:"outer-display-type",text:"Outer display type#"},{depth:3,slug:"inner-display-type-1",text:"Inner display type#"},{depth:2,slug:"outer-display-type-1",text:"Outer display type#"},{depth:3,slug:"display-block",text:"display: block;#"},{depth:3,slug:"display-inline",text:"display: inline;#"},{depth:3,slug:"display-inline-block",text:"display: inline-block#"},{depth:2,slug:"️hiding-elements",text:"🏷️Hiding Elements#"},{depth:3,slug:"display-none",text:"display: none#"},{depth:2,slug:"️floats",text:"🏷️Floats#"},{depth:3,slug:"display-flow-root",text:"display: flow-root#"},{depth:2,slug:"️flexbox",text:"🏷️Flexbox#"},{depth:3,slug:"display-flex",text:"display: flex#"},{depth:3,slug:"inline-flex",text:"inline-flex#"},{depth:3,slug:"contents",text:"contents#"},{depth:2,slug:"️grids",text:"🏷️Grids#"},{depth:3,slug:"display-grid",text:"display: grid#"},{depth:3,slug:"inline-grid",text:"inline-grid#"},{depth:2,slug:"️table-style",text:"🏷️Table Style#"},{depth:3,slug:"display-table",text:"display: table#"},{depth:3,slug:"display-inline-table",text:"display: inline-table#"},{depth:3,slug:"table-caption",text:"table-caption#"},{depth:3,slug:"table-header-group",text:"table-header-group#"},{depth:3,slug:"table-footer-group",text:"table-footer-group#"},{depth:3,slug:"table-row-group",text:"table-row-group#"},{depth:3,slug:"table-column-group",text:"table-column-group#"},{depth:3,slug:"table-row",text:"table-row#"},{depth:3,slug:"table-column",text:"table-column#"},{depth:3,slug:"table-cell",text:"table-cell#"},{depth:3,slug:"table",text:"Table#"},{depth:2,slug:"️styling-lists",text:"🏷️Styling Lists#"},{depth:3,slug:"display-list-item",text:"display: list-item#"}]}const Content=createComponent(((e,n,a)=>{const{layout:i,...l}=frontmatter;return l.file=file,l.url=url,renderTemplate`${maybeRenderHead()}${unescapeHTML(html)}`}));export{Content,compiledContent,Content as default,file,frontmatter,getHeadings,rawContent,url};