import{createComponent,renderTemplate,maybeRenderHead,unescapeHTML}from"../../../../astro.mjs";import"kleur/colors";import"clsx";import"html-escaper";import"cssesc";const html='<h1 id="base-styles">Base Styles<a class="anchor" href="#base-styles"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h1>\n<h2 id="reference">Reference<a class="anchor" href="#reference"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="normalizecss">📌<a href="https://necolas.github.io/normalize.css/">Normalize.css</a><a class="anchor" href="#normalizecss"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>a very popular stylesheet used as a base by many projects</li>\n<li>are used by many developers to create a set of baseline styles to use on all projects.</li>\n<li>anything different across browsers is set to a consistent default before you do your own work on the CSS</li>\n<li><code>npm install --save normalize.css</code></li>\n</ul>\n<h3 id="preflight-tailwind-base-style-reset"><a href="https://tailwindcss.com/docs/preflight">Preflight</a> (Tailwind Base Style/ Reset)<a class="anchor" href="#preflight-tailwind-base-style-reset"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>📌<a href="https://unpkg.com/tailwindcss@3.3.6/src/css/preflight.css">unpkg.com/tailwindcss@3.3.6/src/css/preflight.css</a></li>\n<li>Default margins are removed</li>\n<li>All heading elements are completely unstyled</li>\n<li>lists are unstyled by default, with no bullets/numbers and no margin or padding</li>\n<li>Images and other replaced elements (like <code>svg</code>, <code>video</code>, <code>canvas</code>, and others) are <code>display: block</code> by default.</li>\n<li>Images and videos are constrained to the parent width\n<ul>\n<li>This prevents them from overflowing their containers and makes them responsive by default</li>\n</ul>\n</li>\n<li>Border styles are reset globally</li>\n<li>Extending Preflight</li>\n<li>Disabling Preflight</li>\n</ul>\n<h2 id="appcss--indexcss"><code>app.css</code> &#x26; <code>index.css</code><a class="anchor" href="#appcss--indexcss"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="httpsblogstackademiccomunderstanding-the-role-of-app-css-and-index-css-in-web-development-3d85a7f86b28"><a href="https://blog.stackademic.com/understanding-the-role-of-app-css-and-index-css-in-web-development-3d85a7f86b28">https://blog.stackademic.com/understanding-the-role-of-app-css-and-index-css-in-web-development-3d85a7f86b28</a><a class="anchor" href="#httpsblogstackademiccomunderstanding-the-role-of-app-css-and-index-css-in-web-development-3d85a7f86b28"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="global-selectors">Global selectors<a class="anchor" href="#global-selectors"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="html"><code>html</code><a class="anchor" href="#html"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>📌Text</li>\n<li><code>font-size: 62.5%</code></li>\n</ul>\n<h3 id="host"><code>:host</code><a class="anchor" href="#host"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>used to target the shadow host of the shadow DOM</li>\n</ul>\n<h3 id="root"><code>:root</code><a class="anchor" href="#root"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>often preferred for defining global styles, variables and custom properties intended to be global or shared across the entire document</li>\n<li>highest specificity level\n<ul>\n<li>takes precedence over other selectors like <code>html</code> or <code>*</code></li>\n</ul>\n</li>\n<li>consistency and readability</li>\n<li>📌<a href="Color-Scheme">Color Scheme</a></li>\n<li>📌<a href="CSS-Selectors">CSS Selectors</a> > <a href="pseudo-classes-selectors-(%E4%BC%AA%E7%B1%BB)">pseudo-classes selectors (伪类)</a></li>\n</ul>\n<h3 id="-before-after"><code>*, ::before, ::after</code><a class="anchor" href="#-before-after"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>📌Border</li>\n<li>📌Border-Box</li>\n</ul>\n<h3 id="body"><code>body</code><a class="anchor" href="#body"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>📌Spacing</li>\n</ul>\n<h2 id="borders">📌<a href="Borders">Borders</a><a class="anchor" href="#borders"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="purpose">💡Purpose<a class="anchor" href="#purpose"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Prevent padding and border from affecting element width. (<a href="https://github.com/mozdevs/cssremedy/issues/4">https://github.com/mozdevs/cssremedy/issues/4</a>)</li>\n<li>Allow adding a border to an element by just adding a <code>border-width</code>. (<a href="https://github.com/tailwindcss/tailwindcss/pull/116">https://github.com/tailwindcss/tailwindcss/pull/116</a>)</li>\n</ul>\n<h3 id="box-sizing-border-box"><code>box-sizing: border-box</code><a class="anchor" href="#box-sizing-border-box"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><a href="https://www.paulirish.com/2012/box-sizing-border-box-ftw/">https://www.paulirish.com/2012/box-sizing-border-box-ftw/</a></li>\n<li>📌<a href="Border-Box-Explained">Border-Box Explained</a></li>\n</ul>\n<h3 id="border-width-0"><code>border-width: 0</code><a class="anchor" href="#border-width-0"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="border-style-solid"><code>border-style: solid</code><a class="anchor" href="#border-style-solid"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="border-color-currentcolor"><code>border-color: currentColor</code><a class="anchor" href="#border-color-currentcolor"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="box-spacing-basics">📌<a href="Box-Spacing-Basics">Box Spacing Basics</a><a class="anchor" href="#box-spacing-basics"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="margin--padding">margin &#x26; padding<a class="anchor" href="#margin--padding"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>For consistency, it is a good idea to set margins and padding to 0 on all elements, then add these back in when styling particular controls</li>\n<li><code>margin: 0</code></li>\n<li><code>padding: 0</code></li>\n</ul>\n<h2 id="text">Text<a class="anchor" href="#text"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="line-height-15"><code>line-height: 1.5</code><a class="anchor" href="#line-height-15"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Use a consistent sensible line-height in all browsers.</li>\n</ul>\n<h3 id="-webkit-text-size-adjust-100"><code>-webkit-text-size-adjust: 100%</code><a class="anchor" href="#-webkit-text-size-adjust-100"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Prevent adjustments of font size after orientation changes in iOS</li>\n</ul>\n<h3 id="text-rendering-optimizelegibility"><code>text-rendering: optimizeLegibility</code><a class="anchor" href="#text-rendering-optimizelegibility"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="-webkit-font-smoothing-antialiased"><code>-webkit-font-smoothing: antialiased</code><a class="anchor" href="#-webkit-font-smoothing-antialiased"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="-moz-osx-font-smoothing-grayscale"><code>-moz-osx-font-smoothing: grayscale;</code><a class="anchor" href="#-moz-osx-font-smoothing-grayscale"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="tab-size">Tab Size<a class="anchor" href="#tab-size"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Use a more readable tab size.</li>\n<li><code>tab-size: 4</code></li>\n<li><code>-moz-tab-size: 4</code></li>\n</ul>\n<h3 id="tap-highlight">Tap Highlight<a class="anchor" href="#tap-highlight"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Disable tap highlights on iOS</li>\n<li><code>-webkit-tap-highlight-color: transparent;</code></li>\n</ul>\n<h3 id="special-elements">Special elements<a class="anchor" href="#special-elements"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>hr</code>\n<ul>\n<li><code>height: 0</code>\n<ul>\n<li>Add the correct height in Firefox.</li>\n</ul>\n</li>\n<li><code>box-sizing: content-box</code>\n<ul>\n<li>Add the correct box sizing in Firefox</li>\n</ul>\n</li>\n<li><code>color: inherit</code>\n<ul>\n<li>Correct the inheritance of border color in Firefox. (<a href="https://bugzilla.mozilla.org/show%5C_bug.cgi?id=190655">https://bugzilla.mozilla.org/show\\_bug.cgi?id=190655</a>)</li>\n</ul>\n</li>\n<li><code>border-top-width: 1px;</code>\n<ul>\n<li>Ensure horizontal rules are visible by default.</li>\n</ul>\n</li>\n<li>❓<code>overflow: visible</code>\n<ul>\n<li>Show the overflow in Edge and IE</li>\n</ul>\n</li>\n</ul>\n</li>\n<li><code>hr, dl, dd, blockquote, figure</code>\n<ul>\n<li><code>margin: 0</code></li>\n</ul>\n</li>\n<li><code>dialogue</code>\n<ul>\n<li><code>padding: 0</code></li>\n</ul>\n</li>\n<li><code>pre, code, kbd, samp</code>\n<ul>\n<li><code>font-family: monospace, monospace</code></li>\n</ul>\n</li>\n<li><code>abbr[title]</code>\n<ul>\n<li><code>text-decoration: underline dotted;</code></li>\n</ul>\n</li>\n<li><code>sub, sup</code>\n<ul>\n<li>Prevent <code>sub</code> and <code>sup</code> elements from affecting the line height in all browsers</li>\n<li><code>font-size: 75%</code></li>\n<li><code>line-height: 0</code></li>\n<li><code>position: relative</code></li>\n<li><code>vertical-align: baseline</code></li>\n<li><code>sub</code>\n<ul>\n<li><code>bottom: -0.25em</code></li>\n</ul>\n</li>\n<li><code>sup</code>\n<ul>\n<li><code>top: -0.5em</code></li>\n</ul>\n</li>\n</ul>\n</li>\n<li><code>small</code>\n<ul>\n<li>Add the correct font size in all browsers</li>\n<li><code>font-size: 80%</code></li>\n</ul>\n</li>\n<li><code>b, strong</code>\n<ul>\n<li>Add the correct font weight in Chrome, Edge, and Safari</li>\n<li><code>font-weight: bolder</code></li>\n</ul>\n</li>\n<li><code>a</code>\n<ul>\n<li><code>background-color: transparent</code></li>\n</ul>\n</li>\n</ul>\n<h3 id="styling-links">📌<a href="Styling-Links">Styling Links</a><a class="anchor" href="#styling-links"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="styling-lists">📌<a href="Styling-Lists">Styling Lists</a><a class="anchor" href="#styling-lists"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="css-overflowing-content">📌<a href="CSS-Overflowing-Content">CSS Overflowing Content</a><a class="anchor" href="#css-overflowing-content"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>overflow: auto;</code></li>\n</ul>\n<h3 id="typography">📌<a href="Typography">Typography</a><a class="anchor" href="#typography"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><a href="S-Font-%E5%AD%97%E4%BD%93">S-Font-字体</a></li>\n</ul>\n<h2 id="css-image-video-and-other-replaced-elements">📌<a href="CSS-Image-Video-and-other-Replaced-elements">CSS Image-Video and other Replaced elements</a><a class="anchor" href="#css-image-video-and-other-replaced-elements"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="img-svg-video-canvas-audio-iframe-embed-object"><code>img, svg, video, canvas, audio, iframe, embed, object</code><a class="anchor" href="#img-svg-video-canvas-audio-iframe-embed-object"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>display: block</code></li>\n<li><code>vertical-align: middle</code>\n<ul>\n<li>to align replaced elements more sensibly by default</li>\n</ul>\n</li>\n</ul>\n<h3 id="img-video"><code>img, video</code><a class="anchor" href="#img-video"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Constrain images and videos to the parent width and preserve their intrinsic aspect ratio. (<a href="https://github.com/mozdevs/cssremedy/issues/14">https://github.com/mozdevs/cssremedy/issues/14</a>)</li>\n<li><code>max-width: 100%</code></li>\n<li><code>height: auto</code></li>\n</ul>\n<h3 id="img"><code>img</code><a class="anchor" href="#img"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Remove the border on images inside links in IE 10</li>\n<li><code>border-style: none;</code></li>\n</ul>\n<h2 id="form-style">📌<a href="Form-Style">Form Style</a><a class="anchor" href="#form-style"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="button-input-optgroup-select-textarea"><code>button, input, optgroup, select, textarea</code><a class="anchor" href="#button-input-optgroup-select-textarea"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Change the font styles in all browsers\n<ul>\n<li><code>font-family: inherit;</code></li>\n<li><code>font-size: 100%</code></li>\n<li><code>font-feature-settings: inherit</code></li>\n<li><code>font-variation-settings: inherit</code></li>\n<li><code>line-height: inherit</code></li>\n<li><code>font-weight: inherit</code></li>\n<li><code>color: inherit</code></li>\n</ul>\n</li>\n<li><code>margin: 0</code>\n<ul>\n<li>Remove the margin in Firefox and Safari</li>\n</ul>\n</li>\n<li><code>padding: 0</code>\n<ul>\n<li>Remove default padding in all browsers</li>\n</ul>\n</li>\n</ul>\n<h3 id="button">Button<a class="anchor" href="#button"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>button, [type="button"], [type="reset"],[type="submit"]</code>\n<ul>\n<li><code>-webkit-appearance: button</code>\n<ul>\n<li>Correct the inability to style clickable types in iOS and Safari</li>\n</ul>\n</li>\n<li>Remove default button styles\n<ul>\n<li><code>background-color: transparent</code></li>\n<li><code>background-image: none</code></li>\n</ul>\n</li>\n</ul>\n</li>\n<li><code>button, select</code>\n<ul>\n<li><code>text-transform: none</code>\n<ul>\n<li>Remove the inheritance of text transform in Edge and Firefox</li>\n</ul>\n</li>\n</ul>\n</li>\n<li><code>::-webkit-file-upload-button</code>\n<ul>\n<li><code>-webkit-appearance: button</code></li>\n<li><code>font: inherit</code></li>\n</ul>\n</li>\n<li><code>:-moz-focusring</code>\n<ul>\n<li><code>outline:auto</code>\n<ul>\n<li>Use the modern Firefox focus style for all focusable elements</li>\n</ul>\n</li>\n</ul>\n</li>\n<li><code>:-moz-ui-invalid</code>\n<ul>\n<li><code>box-shadow: none</code>\n<ul>\n<li>Remove the additional <code>:invalid</code> styles in Firefox. (<a href="https://github.com/mozilla/gecko-dev/blob/2f9eacd9d3d995c937b4251a5557d95d494c9be1/layout/style/res/forms.css#L728-L737">https://github.com/mozilla/gecko-dev/blob/2f9eacd9d3d995c937b4251a5557d95d494c9be1/layout/style/res/forms.css#L728-L737</a>)</li>\n</ul>\n</li>\n</ul>\n</li>\n<li>Cursor Style\n<ul>\n<li><code>button, [role="button"]</code>\n<ul>\n<li><code>cursor:pointer</code></li>\n</ul>\n</li>\n<li><code>:disabled</code>\n<ul>\n<li><code>cursor: default</code></li>\n</ul>\n</li>\n<li>Correct the cursor style of increment and decrement buttons in Safari</li>\n<li><code>::-webkit-inner-spin-button, ::-webkit-outer-spin-button</code>\n<ul>\n<li><code>height: auto</code></li>\n</ul>\n</li>\n</ul>\n</li>\n</ul>\n<h3 id="progress"><code>progress</code><a class="anchor" href="#progress"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>vertical-align: baseline</code></li>\n</ul>\n<h3 id="typesearch"><code>[type=\'search\']</code><a class="anchor" href="#typesearch"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>-webkit-appearance: textfield</code>\n<ul>\n<li>Correct the odd appearance in Chrome and Safari</li>\n</ul>\n</li>\n<li><code>outline-offset: -2px</code>\n<ul>\n<li>Correct the outline style in Safari</li>\n</ul>\n</li>\n</ul>\n<h3 id="-webkit-search-decoration"><code>::-webkit-search-decoration</code><a class="anchor" href="#-webkit-search-decoration"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Remove the inner padding in Chrome and Safari on macOS</li>\n<li><code>-webkit-appearance: none</code></li>\n</ul>\n<h3 id="fieldset"><code>fieldset</code><a class="anchor" href="#fieldset"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>margin: 0; padding: 0</code></li>\n</ul>\n<h3 id="legend"><code>legend</code><a class="anchor" href="#legend"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>padding: 0</code></li>\n</ul>\n<h3 id="textarea"><code>textarea</code><a class="anchor" href="#textarea"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>resize: vertical</code></li>\n</ul>\n<h2 id="table-style">📌<a href="Table-Style">Table Style</a><a class="anchor" href="#table-style"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="text-indent0"><code>text-indent:0</code><a class="anchor" href="#text-indent0"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Remove text indentation from table contents in Chrome and Safari (<a href="https://bugs.chromium.org/p/chromium/issues/detail?id=999088">https://bugs.chromium.org/p/chromium/issues/detail?id=999088</a>, <a href="https://bugs.webkit.org/show_bug.cgi?id=201297">https://bugs.webkit.org/show_bug.cgi?id=201297</a>)</li>\n</ul>\n<h3 id="border-color-inherit"><code>border-color: inherit</code><a class="anchor" href="#border-color-inherit"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Correct table border color inheritance in all Chrome and Safari. (<a href="https://bugs.chromium.org/p/chromium/issues/detail?id=935729">https://bugs.chromium.org/p/chromium/issues/detail?id=935729</a>, <a href="https://bugs.webkit.org/show%5C_bug.cgi?id=195016">https://bugs.webkit.org/show\\_bug.cgi?id=195016</a>)</li>\n</ul>\n<h3 id="border-collapse-collapse"><code>border-collapse: collapse</code><a class="anchor" href="#border-collapse-collapse"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Remove gaps between table borders by default</li>\n</ul>\n<h2 id="interactive-display">Interactive Display<a class="anchor" href="#interactive-display"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="summary"><code>summary</code><a class="anchor" href="#summary"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>display: list-item</code></li>\n</ul>\n<h3 id="details"><code>details</code><a class="anchor" href="#details"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>display: block</code></li>\n</ul>\n<h2 id="color">Color<a class="anchor" href="#color"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="s-color-颜色与背景"><a href="S-Color-%E9%A2%9C%E8%89%B2%E4%B8%8E%E8%83%8C%E6%99%AF">S-Color-颜色与背景</a><a class="anchor" href="#s-color-颜色与背景"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="color-scheme">📌<a href="Color-Scheme">Color Scheme</a><a class="anchor" href="#color-scheme"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="scroll-behavior-smooth--auto"><code>scroll-behavior: smooth / auto</code><a class="anchor" href="#scroll-behavior-smooth--auto"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h2 id="hidden"><code>[hidden]</code><a class="anchor" href="#hidden"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-none"><code>display: none</code><a class="anchor" href="#display-none"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="template"><code>template</code><a class="anchor" href="#template"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="display-none-1"><code>display: none</code><a class="anchor" href="#display-none-1"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>',frontmatter={category:"Web Design",aliases:["Base Styles"],draft:!1,title:"Base Styles",type:"D",tags:["CSS"],DateStarted:"2024-01-08T00:00:00.000Z",DateModified:"2024-04-19T00:00:00.000Z",status:null,topic:"Basics",difficulty:"Good",comment:"⭐⭐⭐",Datereviewed:"2024-01-14T16:00:00.000Z",reviewed:2,"linter-yaml-title-alias":"Base Styles",minutes:3,words:574},file="E:/SynologyDrive/Codespace/blog-site/mynote/my-fuwari-blog/src/content/posts/css/basics/base-styles.md",url=void 0;function rawContent(){return'\n# Base Styles\n\n## Reference\n\n### 📌[Normalize.css](https://necolas.github.io/normalize.css/)\n\n- a very popular stylesheet used as a base by many projects\n- are used by many developers to create a set of baseline styles to use on all projects.\n- anything different across browsers is set to a consistent default before you do your own work on the CSS\n- `npm install --save normalize.css`\n\n### [Preflight](https://tailwindcss.com/docs/preflight) (Tailwind Base Style/ Reset)\n\n- 📌[unpkg.com/tailwindcss@3.3.6/src/css/preflight.css](https://unpkg.com/tailwindcss@3.3.6/src/css/preflight.css)\n- Default margins are removed\n- All heading elements are completely unstyled\n- lists are unstyled by default, with no bullets/numbers and no margin or padding\n- Images and other replaced elements (like `svg`, `video`, `canvas`, and others) are `display: block` by default.\n- Images and videos are constrained to the parent width\n  - This prevents them from overflowing their containers and makes them responsive by default\n- Border styles are reset globally\n- Extending Preflight\n- Disabling Preflight\n\n## `app.css` & `index.css`\n\n### https://blog.stackademic.com/understanding-the-role-of-app-css-and-index-css-in-web-development-3d85a7f86b28\n\n## Global selectors\n\n### `html`\n\n- 📌Text\n- `font-size: 62.5%`\n\n### `:host`\n\n- used to target the shadow host of the shadow DOM\n\n### `:root`\n\n- often preferred for defining global styles, variables and custom properties intended to be global or shared across the entire document\n- highest specificity level\n  - takes precedence over other selectors like `html` or `*`\n- consistency and readability\n- 📌[Color Scheme](Color-Scheme)\n- 📌[CSS Selectors](CSS-Selectors) > [pseudo-classes selectors (伪类)](<pseudo-classes-selectors-(伪类)>)\n\n### `*, ::before, ::after`\n\n- 📌Border\n- 📌Border-Box\n\n### `body`\n\n- 📌Spacing\n\n## 📌[Borders](Borders)\n\n### 💡Purpose\n\n- Prevent padding and border from affecting element width. (https://github.com/mozdevs/cssremedy/issues/4)\n- Allow adding a border to an element by just adding a `border-width`. (https://github.com/tailwindcss/tailwindcss/pull/116)\n\n### `box-sizing: border-box`\n\n- https://www.paulirish.com/2012/box-sizing-border-box-ftw/\n- 📌[Border-Box Explained](Border-Box-Explained)\n\n### `border-width: 0`\n\n### `border-style: solid`\n\n### `border-color: currentColor`\n\n## 📌[Box Spacing Basics](Box-Spacing-Basics)\n\n### margin & padding\n\n- For consistency, it is a good idea to set margins and padding to 0 on all elements, then add these back in when styling particular controls\n- `margin: 0`\n- `padding: 0`\n\n## Text\n\n### `line-height: 1.5`\n\n- Use a consistent sensible line-height in all browsers.\n\n### `-webkit-text-size-adjust: 100%`\n\n- Prevent adjustments of font size after orientation changes in iOS\n\n### `text-rendering: optimizeLegibility`\n\n### `-webkit-font-smoothing: antialiased`\n\n### `-moz-osx-font-smoothing: grayscale;`\n\n### Tab Size\n\n- Use a more readable tab size.\n- `tab-size: 4`\n- `-moz-tab-size: 4`\n\n### Tap Highlight\n\n- Disable tap highlights on iOS\n- `-webkit-tap-highlight-color: transparent;`\n\n### Special elements\n\n- `hr`\n  - `height: 0`\n    - Add the correct height in Firefox.\n  - `box-sizing: content-box`\n    - Add the correct box sizing in Firefox\n  - `color: inherit`\n    - Correct the inheritance of border color in Firefox. (https://bugzilla.mozilla.org/show\\_bug.cgi?id=190655)\n  - `border-top-width: 1px;`\n    - Ensure horizontal rules are visible by default.\n  - ❓`overflow: visible`\n    - Show the overflow in Edge and IE\n- `hr, dl, dd, blockquote, figure`\n  - `margin: 0`\n- `dialogue`\n  - `padding: 0`\n- `pre, code, kbd, samp`\n  - `font-family: monospace, monospace`\n- `abbr[title]`\n  - `text-decoration: underline dotted;`\n- `sub, sup`\n  - Prevent `sub` and `sup` elements from affecting the line height in all browsers\n  - `font-size: 75%`\n  - `line-height: 0`\n  - `position: relative`\n  - `vertical-align: baseline`\n  - `sub`\n    - `bottom: -0.25em`\n  - `sup`\n    - `top: -0.5em`\n- `small`\n  - Add the correct font size in all browsers\n  - `font-size: 80%`\n- `b, strong`\n  - Add the correct font weight in Chrome, Edge, and Safari\n  - `font-weight: bolder`\n- `a`\n  - `background-color: transparent`\n\n### 📌[Styling Links](Styling-Links)\n\n### 📌[Styling Lists](Styling-Lists)\n\n### 📌[CSS Overflowing Content](CSS-Overflowing-Content)\n\n- `overflow: auto;`\n\n### 📌[Typography](Typography)\n\n- [S-Font-字体](S-Font-字体)\n\n## 📌[CSS Image-Video and other Replaced elements](CSS-Image-Video-and-other-Replaced-elements)\n\n### `img, svg, video, canvas, audio, iframe, embed, object`\n\n- `display: block`\n- `vertical-align: middle`\n  - to align replaced elements more sensibly by default\n\n### `img, video`\n\n- Constrain images and videos to the parent width and preserve their intrinsic aspect ratio. (https://github.com/mozdevs/cssremedy/issues/14)\n- `max-width: 100%`\n- `height: auto`\n\n### `img`\n\n- Remove the border on images inside links in IE 10\n- `border-style: none;`\n\n## 📌[Form Style](Form-Style)\n\n### `button, input, optgroup, select, textarea`\n\n- Change the font styles in all browsers\n  - `font-family: inherit;`\n  - `font-size: 100%`\n  - `font-feature-settings: inherit`\n  - `font-variation-settings: inherit`\n  - `line-height: inherit`\n  - `font-weight: inherit`\n  - `color: inherit`\n- `margin: 0`\n  - Remove the margin in Firefox and Safari\n- `padding: 0`\n  - Remove default padding in all browsers\n\n### Button\n\n- `button, [type="button"], [type="reset"],[type="submit"]`\n  - `-webkit-appearance: button`\n    - Correct the inability to style clickable types in iOS and Safari\n  - Remove default button styles\n    - `background-color: transparent`\n    - `background-image: none`\n- `button, select`\n  - `text-transform: none`\n    - Remove the inheritance of text transform in Edge and Firefox\n- `::-webkit-file-upload-button`\n  - `-webkit-appearance: button`\n  - `font: inherit`\n- `:-moz-focusring`\n  - `outline:auto`\n    - Use the modern Firefox focus style for all focusable elements\n- `:-moz-ui-invalid`\n  - `box-shadow: none`\n    - Remove the additional `:invalid` styles in Firefox. (https://github.com/mozilla/gecko-dev/blob/2f9eacd9d3d995c937b4251a5557d95d494c9be1/layout/style/res/forms.css#L728-L737)\n- Cursor Style\n  - `button, [role="button"]`\n    - `cursor:pointer`\n  - `:disabled`\n    - `cursor: default`\n  - Correct the cursor style of increment and decrement buttons in Safari\n  - `::-webkit-inner-spin-button, ::-webkit-outer-spin-button`\n    - `height: auto`\n\n### `progress`\n\n- `vertical-align: baseline`\n\n### `[type=\'search\']`\n\n- `-webkit-appearance: textfield`\n  - Correct the odd appearance in Chrome and Safari\n- `outline-offset: -2px`\n  - Correct the outline style in Safari\n\n### `::-webkit-search-decoration`\n\n- Remove the inner padding in Chrome and Safari on macOS\n- `-webkit-appearance: none`\n\n### `fieldset`\n\n- `margin: 0; padding: 0`\n\n### `legend`\n\n- `padding: 0`\n\n### `textarea`\n\n- `resize: vertical`\n\n## 📌[Table Style](Table-Style)\n\n### `text-indent:0`\n\n- Remove text indentation from table contents in Chrome and Safari (https://bugs.chromium.org/p/chromium/issues/detail?id=999088, https://bugs.webkit.org/show_bug.cgi?id=201297)\n\n### `border-color: inherit`\n\n- Correct table border color inheritance in all Chrome and Safari. (https://bugs.chromium.org/p/chromium/issues/detail?id=935729, https://bugs.webkit.org/show\\_bug.cgi?id=195016)\n\n### `border-collapse: collapse`\n\n- Remove gaps between table borders by default\n\n## Interactive Display\n\n### `summary`\n\n- `display: list-item`\n\n### `details`\n\n- `display: block`\n\n## Color\n\n### [S-Color-颜色与背景](S-Color-颜色与背景)\n\n### 📌[Color Scheme](Color-Scheme)\n\n## `scroll-behavior: smooth / auto`\n\n## `[hidden]`\n\n### `display: none`\n\n## `template`\n\n### `display: none`\n'}function compiledContent(){return html}function getHeadings(){return[{depth:1,slug:"base-styles",text:"Base Styles#"},{depth:2,slug:"reference",text:"Reference#"},{depth:3,slug:"normalizecss",text:"📌Normalize.css#"},{depth:3,slug:"preflight-tailwind-base-style-reset",text:"Preflight (Tailwind Base Style/ Reset)#"},{depth:2,slug:"appcss--indexcss",text:"app.css & index.css#"},{depth:3,slug:"httpsblogstackademiccomunderstanding-the-role-of-app-css-and-index-css-in-web-development-3d85a7f86b28",text:"https://blog.stackademic.com/understanding-the-role-of-app-css-and-index-css-in-web-development-3d85a7f86b28#"},{depth:2,slug:"global-selectors",text:"Global selectors#"},{depth:3,slug:"html",text:"html#"},{depth:3,slug:"host",text:":host#"},{depth:3,slug:"root",text:":root#"},{depth:3,slug:"-before-after",text:"*, ::before, ::after#"},{depth:3,slug:"body",text:"body#"},{depth:2,slug:"borders",text:"📌Borders#"},{depth:3,slug:"purpose",text:"💡Purpose#"},{depth:3,slug:"box-sizing-border-box",text:"box-sizing: border-box#"},{depth:3,slug:"border-width-0",text:"border-width: 0#"},{depth:3,slug:"border-style-solid",text:"border-style: solid#"},{depth:3,slug:"border-color-currentcolor",text:"border-color: currentColor#"},{depth:2,slug:"box-spacing-basics",text:"📌Box Spacing Basics#"},{depth:3,slug:"margin--padding",text:"margin & padding#"},{depth:2,slug:"text",text:"Text#"},{depth:3,slug:"line-height-15",text:"line-height: 1.5#"},{depth:3,slug:"-webkit-text-size-adjust-100",text:"-webkit-text-size-adjust: 100%#"},{depth:3,slug:"text-rendering-optimizelegibility",text:"text-rendering: optimizeLegibility#"},{depth:3,slug:"-webkit-font-smoothing-antialiased",text:"-webkit-font-smoothing: antialiased#"},{depth:3,slug:"-moz-osx-font-smoothing-grayscale",text:"-moz-osx-font-smoothing: grayscale;#"},{depth:3,slug:"tab-size",text:"Tab Size#"},{depth:3,slug:"tap-highlight",text:"Tap Highlight#"},{depth:3,slug:"special-elements",text:"Special elements#"},{depth:3,slug:"styling-links",text:"📌Styling Links#"},{depth:3,slug:"styling-lists",text:"📌Styling Lists#"},{depth:3,slug:"css-overflowing-content",text:"📌CSS Overflowing Content#"},{depth:3,slug:"typography",text:"📌Typography#"},{depth:2,slug:"css-image-video-and-other-replaced-elements",text:"📌CSS Image-Video and other Replaced elements#"},{depth:3,slug:"img-svg-video-canvas-audio-iframe-embed-object",text:"img, svg, video, canvas, audio, iframe, embed, object#"},{depth:3,slug:"img-video",text:"img, video#"},{depth:3,slug:"img",text:"img#"},{depth:2,slug:"form-style",text:"📌Form Style#"},{depth:3,slug:"button-input-optgroup-select-textarea",text:"button, input, optgroup, select, textarea#"},{depth:3,slug:"button",text:"Button#"},{depth:3,slug:"progress",text:"progress#"},{depth:3,slug:"typesearch",text:"[type='search']#"},{depth:3,slug:"-webkit-search-decoration",text:"::-webkit-search-decoration#"},{depth:3,slug:"fieldset",text:"fieldset#"},{depth:3,slug:"legend",text:"legend#"},{depth:3,slug:"textarea",text:"textarea#"},{depth:2,slug:"table-style",text:"📌Table Style#"},{depth:3,slug:"text-indent0",text:"text-indent:0#"},{depth:3,slug:"border-color-inherit",text:"border-color: inherit#"},{depth:3,slug:"border-collapse-collapse",text:"border-collapse: collapse#"},{depth:2,slug:"interactive-display",text:"Interactive Display#"},{depth:3,slug:"summary",text:"summary#"},{depth:3,slug:"details",text:"details#"},{depth:2,slug:"color",text:"Color#"},{depth:3,slug:"s-color-颜色与背景",text:"S-Color-颜色与背景#"},{depth:3,slug:"color-scheme",text:"📌Color Scheme#"},{depth:2,slug:"scroll-behavior-smooth--auto",text:"scroll-behavior: smooth / auto#"},{depth:2,slug:"hidden",text:"[hidden]#"},{depth:3,slug:"display-none",text:"display: none#"},{depth:2,slug:"template",text:"template#"},{depth:3,slug:"display-none-1",text:"display: none#"}]}const Content=createComponent(((e,n,a)=>{const{layout:o,...i}=frontmatter;return i.file=file,i.url=url,renderTemplate`${maybeRenderHead()}${unescapeHTML(html)}`}));export{Content,compiledContent,Content as default,file,frontmatter,getHeadings,rawContent,url};