import{createComponent,renderTemplate,maybeRenderHead,unescapeHTML}from"../../../../astro.mjs";import"kleur/colors";import"clsx";import"html-escaper";import"cssesc";const html='<h1 id="styling-tables">Styling Tables<a class="anchor" href="#styling-tables"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h1>\n<h2 id="a-typical-html-table">A typical HTML table<a class="anchor" href="#a-typical-html-table"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="httpsdevelopermozillaorgen-usdocslearncssbuilding_blocksstyling_tables"><a href="https://developer.mozilla.org/en-US/docs/Learn/CSS/Building_blocks/Styling_tables">https://developer.mozilla.org/en-US/docs/Learn/CSS/Building_blocks/Styling_tables</a><a class="anchor" href="#httpsdevelopermozillaorgen-usdocslearncssbuilding_blocksstyling_tables"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="table"><strong><code>&#x3C;table></code></strong><a class="anchor" href="#table"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><strong><code>&#x3C;caption></code></strong></li>\n<li><strong><code>thead</code></strong>\n<ul>\n<li><code>&#x3C;tr></code></li>\n<li><code>&#x3C;th scope ="col"></code></li>\n</ul>\n</li>\n<li><strong><code>tbody</code></strong>\n<ul>\n<li><code>&#x3C;th scope="row"></code></li>\n<li><code>&#x3C;td></code></li>\n</ul>\n</li>\n<li><strong><code>tfoot</code></strong>\n<ul>\n<li><code>&#x3C;td colspan="2"></code></li>\n</ul>\n</li>\n</ul>\n<h2 id="styling-our-table">Styling our table<a class="anchor" href="#styling-our-table"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="spacing-and-layout">Spacing and layout<a class="anchor" href="#spacing-and-layout"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><strong><code>table-layout: fixed</code></strong>\n<ul>\n<li>makes the table behave a bit more predictably by default</li>\n<li>Normally, table columns tend to be sized according to how much content they contain, which produces some strange results</li>\n</ul>\n</li>\n<li><strong><code>border-collapse: collapse;</code></strong>\n<ul>\n<li><img src="https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/Paste-image-1695300802216image.png" alt=""></li>\n</ul>\n</li>\n<li><strong><code>thead th:nth-child(n)</code></strong>\n<ul>\n<li>Select the n-th child that is a <code>&#x3C;th></code> element in a sequence, inside a <code>&#x3C;thead></code> element</li>\n</ul>\n</li>\n<li>üìå<a href="https://css-tricks.com/fixing-tables-long-strings/">Fixed Table Layout</a></li>\n</ul>\n<h3 id="typography">Typography<a class="anchor" href="#typography"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><a href="../../Typography/Typography">Typography</a></li>\n</ul>\n<h3 id="graphics-and-colors">Graphics and colors<a class="anchor" href="#graphics-and-colors"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>Zebra striping\n<ul>\n<li>make alternative rows easier to read</li>\n<li><strong><code>tbody tr:nth-child(odd) { }</code></strong>\n<ul>\n<li>=> <code>(2n-1)</code></li>\n</ul>\n</li>\n<li><strong><code>tbody tr:nth-child(even) { } </code></strong>\n<ul>\n<li>=> <code>(2n)</code></li>\n</ul>\n</li>\n<li>Refer to: <a href="CSS-Selectors">CSS Selectors</a></li>\n</ul>\n</li>\n</ul>\n<h3 id="styling-the-caption">Styling the caption<a class="anchor" href="#styling-the-caption"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><strong><code>caption-side: bottom</code></strong>\n<ul>\n<li>causes the caption to be positioned on the bottom of the table</li>\n</ul>\n</li>\n</ul>',frontmatter={category:"Web Design",aliases:["Styling Tables"],draft:!1,title:"Styling Tables",type:"D",tags:["CSS"],status:null,DateStarted:"2023-09-21T00:00:00.000Z",DateModified:"2024-04-19T00:00:00.000Z",Datereviewed:"2024-01-18T16:00:00.000Z",reviewed:2,difficulty:"Hard",topic:["Layout"],comment:"‚≠ê‚≠ê","linter-yaml-title-alias":"Styling Tables",minutes:1,words:94},file="E:/SynologyDrive/Codespace/blog-site/mynote/my-fuwari-blog/src/content/posts/css/layout/table-style.md",url=void 0;function rawContent(){return'\n# Styling Tables\n\n## A typical HTML table\n\n### https://developer.mozilla.org/en-US/docs/Learn/CSS/Building_blocks/Styling_tables\n\n### **`<table>`**\n\n- **`<caption>`**\n- **`thead`**\n  - `<tr>`\n  - `<th scope ="col">`\n- **`tbody`**\n  - `<th scope="row">`\n  - `<td>`\n- **`tfoot`**\n  - `<td colspan="2">`\n\n## Styling our table\n\n### Spacing and layout\n\n- **`table-layout: fixed`**\n  - makes the table behave a bit more predictably by default\n  - Normally, table columns tend to be sized according to how much content they contain, which produces some strange results\n- **`border-collapse: collapse;`**\n  - ![](https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/Paste-image-1695300802216image.png)\n- **`thead th:nth-child(n)`**\n  - Select the n-th child that is a `<th>` element in a sequence, inside a `<thead>` element\n- üìå[Fixed Table Layout](https://css-tricks.com/fixing-tables-long-strings/)\n\n### Typography\n\n- [Typography](../../Typography/Typography)\n\n### Graphics and colors\n\n- Zebra striping\n  - make alternative rows easier to read\n  - **`tbody tr:nth-child(odd) { }`**\n    - => `(2n-1)`\n  - **`tbody tr:nth-child(even) { } `**\n    - => `(2n)`\n  - Refer to: [CSS Selectors](CSS-Selectors)\n\n### Styling the caption\n\n- **`caption-side: bottom`**\n  - causes the caption to be positioned on the bottom of the table\n'}function compiledContent(){return html}function getHeadings(){return[{depth:1,slug:"styling-tables",text:"Styling Tables#"},{depth:2,slug:"a-typical-html-table",text:"A typical HTML table#"},{depth:3,slug:"httpsdevelopermozillaorgen-usdocslearncssbuilding_blocksstyling_tables",text:"https://developer.mozilla.org/en-US/docs/Learn/CSS/Building_blocks/Styling_tables#"},{depth:3,slug:"table",text:"<table>#"},{depth:2,slug:"styling-our-table",text:"Styling our table#"},{depth:3,slug:"spacing-and-layout",text:"Spacing and layout#"},{depth:3,slug:"typography",text:"Typography#"},{depth:3,slug:"graphics-and-colors",text:"Graphics and colors#"},{depth:3,slug:"styling-the-caption",text:"Styling the caption#"}]}const Content=createComponent(((n,e,t)=>{const{layout:a,...l}=frontmatter;return l.file=file,l.url=url,renderTemplate`${maybeRenderHead()}${unescapeHTML(html)}`}));export{Content,compiledContent,Content as default,file,frontmatter,getHeadings,rawContent,url};