import{createComponent,renderTemplate,maybeRenderHead,unescapeHTML}from"../../../../astro.mjs";import"kleur/colors";import"clsx";import"html-escaper";import"cssesc";const html='<h1 id="sync-and-publish-obsidian-notes-with-astro">Sync and Publish Obsidian Notes with Astro<a class="anchor" href="#sync-and-publish-obsidian-notes-with-astro"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h1>\n<h2 id="purpose">Purpose<a class="anchor" href="#purpose"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<ul class="contains-task-list">\n<li class="task-list-item"><input type="checkbox" checked disabled> Public notes from different obsidian vaults keep in sync with public notes in Astro project</li>\n<li class="task-list-item"><input type="checkbox" checked disabled> wikilink/ markdown link can be parsed properly</li>\n</ul>\n<h2 id="problems">Problems<a class="anchor" href="#problems"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<p>Astro can’t recognize:</p>\n<ul>\n<li>wikilinks like\n<ul>\n<li><code>![[image.png]]</code></li>\n<li><code>[[note]]</code></li>\n</ul>\n</li>\n<li>name with empty space placeholder\n<ul>\n<li><code>-</code></li>\n</ul>\n</li>\n<li>common markdown links like <code>[another](another)</code>\n<ul>\n<li>the internal link to another markdown file has to be rewritten as <code>[another](another)</code> which is unrecognizable for other markdown editor.</li>\n</ul>\n</li>\n</ul>\n<h2 id="solutions">Solutions<a class="anchor" href="#solutions"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="for-sync-obsidian-vaults-to-astro-project">For Sync Obsidian Vaults to Astro project<a class="anchor" href="#for-sync-obsidian-vaults-to-astro-project"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<p>Use <strong>mklink/ symlink</strong></p>\n<ul>\n<li>✅Update stays in sync</li>\n<li>✅Obsidian vault doesn’t get bloated with <code>node_modules</code> and files other than notes</li>\n</ul>\n<h3 id="for-image-links">For image links<a class="anchor" href="#for-image-links"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h4 id="-solution-1-update-local-images">❌ Solution-1: Update local images<a class="anchor" href="#-solution-1-update-local-images"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h4>\n<ul>\n<li>change them to markdown link format (using obsidian wikilink to markdown link plugin)\n<ul>\n<li><code>![image](image.png)</code></li>\n</ul>\n</li>\n<li>replace empty space in image name to <code>-</code></li>\n<li>replace <code>-</code> in image markdown link to <code>-</code></li>\n<li>add a <code>./</code> before the image link,</li>\n</ul>\n<h4 id="-solution-2-use-free-pic-bed">👍 Solution-2: Use free pic-bed<a class="anchor" href="#-solution-2-use-free-pic-bed"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h4>\n<ul class="contains-task-list">\n<li class="task-list-item"><input type="checkbox" checked disabled> 配置 PicList 上传图片压缩 ✅ 2024-04-15</li>\n<li class="task-list-item"><input type="checkbox" checked disabled> 批量压缩图片 (洋芋田图像处理软件) ✅ 2024-04-16</li>\n<li class="task-list-item"><input type="checkbox" checked disabled> 克隆图片仓库，批量上传笔记图片至 GitHub，批量更新笔记图片链接 ✅ 2024-04-17</li>\n<li class="task-list-item"><input type="checkbox" checked disabled> 批量替换笔记内图片链接： ✅ 2024-04-17\n<ul>\n<li><code>![](https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/full-stack/image.png)</code></li>\n</ul>\n</li>\n</ul>\n<h4 id="ultimate-solution-reduce-image-use">Ultimate Solution: Reduce image use<a class="anchor" href="#ultimate-solution-reduce-image-use"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h4>\n<blockquote>\n<p>终极方案：减少不必要的笔记图片，专注文本输出</p>\n</blockquote>\n<h3 id="for-note-links">For note links<a class="anchor" href="#for-note-links"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h4 id="-solutions-ive-tried">❌ Solutions I’ve tried<a class="anchor" href="#-solutions-ive-tried"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h4>\n<blockquote>\n<p>neither of the following solution works in astro-starlight project</p>\n</blockquote>\n<ul>\n<li><a href="https://github.com/vernak2539/astro-rehype-relative-markdown-links">GitHub - vernak2539/astro-rehype-relative-markdown-links: Rehype Plugin for Astro that allows for usage of relative links between markdown files</a></li>\n<li><a href="https://starlight-links-validator.vercel.app/getting-started/">Getting Started | Starlight Links Validator</a></li>\n</ul>\n<h5 id="starlight-obsidian">starlight-obsidian<a class="anchor" href="#starlight-obsidian"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h5>\n<blockquote>\n<p><a href="https://github.com/HiDeoo/starlight-obsidian">GitHub - HiDeoo/starlight-obsidian: Starlight plugin to publish Obsidian vaults</a></p>\n</blockquote>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n<table><thead><tr><th>Pros</th><th>Cons</th></tr></thead><tbody><tr><td>✅ Supports wikilink to astro-link</td><td>❌ can only link to 1 obsidian vault (not able to publish multiple vaults)</td></tr><tr><td></td><td>❌ a <code>Note</code> item in Sidebar can’t be removed</td></tr><tr><td></td><td>❌ duplicated notes in astro-project <code>public</code> folder (bloat disk space)</td></tr><tr><td></td><td><br><br></td></tr></tbody></table>\n<h4 id="-current-solution-im-using">👍 Current solution I’m using<a class="anchor" href="#-current-solution-im-using"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h4>\n<ul>\n<li><a href="https://stackoverflow.com/questions/76163067/using-markdown-wiki-links-in-astro-framework">parsing - Using markdown wiki-links in Astro framework - Stack Overflow</a></li>\n<li>@portaljs/remark-wiki-link<br>\nTurn note links into absolute path wikilink<br>\nConfigure <code>@portaljs/remark-wiki-link</code></li>\n</ul>',frontmatter={category:"Front-End Frameworks",title:"Sync and Publish Obsidian Notes with Astro",topic:null,type:null,tags:["Astro"],DateStarted:"2024-04-07T00:00:00.000Z",DateModified:"2024-04-18T00:00:00.000Z",Datereviewed:null,reviewed:null,difficulty:null,status:null,comment:null,aliases:["Sync and Publish Obsidian Notes with Astro","Sync and Publish Obsidian Notes with Astro"],"linter-yaml-title-alias":"Sync and Publish Obsidian Notes with Astro",minutes:2,words:312},file="E:/SynologyDrive/Codespace/blog-site/mynote/my-fuwari-blog/src/content/posts/front-end-frameworks/astro/sync-and-publish-obsidian-notes-with-astro.md",url=void 0;function rawContent(){return"\n# Sync and Publish Obsidian Notes with Astro\n\n## Purpose\n\n- [x] Public notes from different obsidian vaults keep in sync with public notes in Astro project\n- [x] wikilink/ markdown link can be parsed properly\n\n## Problems\n\nAstro can't recognize:\n\n- wikilinks like\n  - `![[image.png]]`\n  - `[[note]]`\n- name with empty space placeholder\n  - `-`\n- common markdown links like `[another](another)`\n  - the internal link to another markdown file has to be rewritten as `[another](another)` which is unrecognizable for other markdown editor.\n\n## Solutions\n\n### For Sync Obsidian Vaults to Astro project\n\nUse **mklink/ symlink**\n\n- ✅Update stays in sync\n- ✅Obsidian vault doesn't get bloated with `node_modules` and files other than notes\n\n### For image links\n\n#### ❌ Solution-1: Update local images\n\n- change them to markdown link format (using obsidian wikilink to markdown link plugin)\n  - `![image](image.png)`\n- replace empty space in image name to `-`\n- replace `-` in image markdown link to `-`\n- add a `./` before the image link,\n\n#### 👍 Solution-2: Use free pic-bed\n\n- [x] 配置 PicList 上传图片压缩 ✅ 2024-04-15\n- [x] 批量压缩图片 (洋芋田图像处理软件) ✅ 2024-04-16\n- [x] 克隆图片仓库，批量上传笔记图片至 GitHub，批量更新笔记图片链接 ✅ 2024-04-17\n- [x] 批量替换笔记内图片链接： ✅ 2024-04-17\n  - `![](https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/full-stack/image.png)`\n\n#### Ultimate Solution: Reduce image use\n\n> 终极方案：减少不必要的笔记图片，专注文本输出\n\n### For note links\n\n#### ❌ Solutions I've tried\n\n> neither of the following solution works in astro-starlight project\n\n- [GitHub - vernak2539/astro-rehype-relative-markdown-links: Rehype Plugin for Astro that allows for usage of relative links between markdown files](https://github.com/vernak2539/astro-rehype-relative-markdown-links)\n- [Getting Started | Starlight Links Validator](https://starlight-links-validator.vercel.app/getting-started/)\n\n##### starlight-obsidian\n\n> [GitHub - HiDeoo/starlight-obsidian: Starlight plugin to publish Obsidian vaults](https://github.com/HiDeoo/starlight-obsidian)\n\n| Pros                               | Cons                                                                       |\n| ---------------------------------- | -------------------------------------------------------------------------- |\n| ✅ Supports wikilink to astro-link | ❌ can only link to 1 obsidian vault (not able to publish multiple vaults) |\n|                                    | ❌ a `Note` item in Sidebar can't be removed                               |\n|                                    | ❌ duplicated notes in astro-project `public` folder (bloat disk space)    |\n|                                    | <br><br>                                                                   |\n\n#### 👍 Current solution I'm using\n\n- [parsing - Using markdown wiki-links in Astro framework - Stack Overflow](https://stackoverflow.com/questions/76163067/using-markdown-wiki-links-in-astro-framework)\n- @portaljs/remark-wiki-link  \n  Turn note links into absolute path wikilink  \n  Configure `@portaljs/remark-wiki-link`\n"}function compiledContent(){return html}function getHeadings(){return[{depth:1,slug:"sync-and-publish-obsidian-notes-with-astro",text:"Sync and Publish Obsidian Notes with Astro#"},{depth:2,slug:"purpose",text:"Purpose#"},{depth:2,slug:"problems",text:"Problems#"},{depth:2,slug:"solutions",text:"Solutions#"},{depth:3,slug:"for-sync-obsidian-vaults-to-astro-project",text:"For Sync Obsidian Vaults to Astro project#"},{depth:3,slug:"for-image-links",text:"For image links#"},{depth:4,slug:"-solution-1-update-local-images",text:"❌ Solution-1: Update local images#"},{depth:4,slug:"-solution-2-use-free-pic-bed",text:"👍 Solution-2: Use free pic-bed#"},{depth:4,slug:"ultimate-solution-reduce-image-use",text:"Ultimate Solution: Reduce image use#"},{depth:3,slug:"for-note-links",text:"For note links#"},{depth:4,slug:"-solutions-ive-tried",text:"❌ Solutions I’ve tried#"},{depth:5,slug:"starlight-obsidian",text:"starlight-obsidian#"},{depth:4,slug:"-current-solution-im-using",text:"👍 Current solution I’m using#"}]}const Content=createComponent(((n,i,t)=>{const{layout:e,...o}=frontmatter;return o.file=file,o.url=url,renderTemplate`${maybeRenderHead()}${unescapeHTML(html)}`}));export{Content,compiledContent,Content as default,file,frontmatter,getHeadings,rawContent,url};