import{createComponent,renderTemplate,maybeRenderHead,unescapeHTML}from"../../../../astro.mjs";import"kleur/colors";import"clsx";import"html-escaper";import"cssesc";const html='<h2 id="border-box-explained">Border-Box Explained<a class="anchor" href="#border-box-explained"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="standard-css-box-model">⭐Standard CSS Box Model<a class="anchor" href="#standard-css-box-model"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><img src="https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/css-box-model-(盒模型)-standard-css-box.png" alt=""></li>\n</ul>\n<h3 id="alternative-css-box-model">⭐Alternative CSS Box Model<a class="anchor" href="#alternative-css-box-model"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>\n<p>border-box</p>\n</li>\n<li>\n<p><code>box-sizing: border-box</code></p>\n<ul>\n<li><img src="https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/css-box-model-(盒模型)-border-box.png" alt=""></li>\n</ul>\n</li>\n<li>\n<p>📌<a href="Base-Styles">Base Styles</a></p>\n</li>\n<li>\n<p>📌To use the alternative box model for all of your elements (which is a common choice among developers)</p>\n<pre class="astro-code github-dark" style="background-color:#24292e;color:#e1e4e8; overflow-x: auto;" tabindex="0"><code><span class="line"><span style="color:#85E89D">html</span><span style="color:#E1E4E8"> {</span></span>\n<span class="line"><span style="color:#79B8FF">\tbox-sizing</span><span style="color:#E1E4E8">: </span><span style="color:#79B8FF">border-box</span><span style="color:#E1E4E8">;</span></span>\n<span class="line"><span style="color:#E1E4E8">}</span></span>\n<span class="line"><span style="color:#85E89D">*</span><span style="color:#E1E4E8">,</span></span>\n<span class="line"><span style="color:#85E89D">*</span><span style="color:#B392F0">::before</span><span style="color:#E1E4E8">,</span></span>\n<span class="line"><span style="color:#85E89D">*</span><span style="color:#B392F0">::after</span><span style="color:#E1E4E8"> {</span></span>\n<span class="line"><span style="color:#79B8FF">\tbox-sizing</span><span style="color:#E1E4E8">: </span><span style="color:#79B8FF">inherit</span><span style="color:#E1E4E8">;</span></span>\n<span class="line"><span style="color:#E1E4E8">}</span></span>\n<span class="line"></span></code></pre>\n<ul>\n<li>separates the reset from the global styling of all elements\n<ul>\n<li>explicitly sets the box-sizing for the root element and lets the rest inherit it</li>\n<li>can be seen as a form of organization, especially if you have other global styles applied to the <code>&#x3C;html></code> element</li>\n</ul>\n</li>\n</ul>\n</li>\n</ul>',frontmatter={category:"Web Design",draft:!1,title:"border-box-explained",type:"D",tags:["CSS"],DateStarted:"2024-01-09T00:00:00.000Z",DateModified:"2024-04-25T00:00:00.000Z",status:null,reviewed:2,Datereviewed:"2024-04-25T00:00:00.000Z",topic:["Box"],difficulty:"Good",comment:"⭐⭐",minutes:1,words:79},file="E:/SynologyDrive/Codespace/blog-site/mynote/my-fuwari-blog/src/content/posts/css/box-model-spacing/border-box-explained.md",url=void 0;function rawContent(){return"\n## Border-Box Explained\n\n### ⭐Standard CSS Box Model\n\n- ![](https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/css-box-model-(盒模型)-standard-css-box.png)\n\n### ⭐Alternative CSS Box Model\n\n- border-box\n- `box-sizing: border-box`\n  - ![](https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/css-box-model-(盒模型)-border-box.png)\n- 📌[Base Styles](Base-Styles)\n- 📌To use the alternative box model for all of your elements (which is a common choice among developers)\n\n  ```css\n  html {\n  \tbox-sizing: border-box;\n  }\n  *,\n  *::before,\n  *::after {\n  \tbox-sizing: inherit;\n  }\n  ```\n\n  - separates the reset from the global styling of all elements\n    - explicitly sets the box-sizing for the root element and lets the rest inherit it\n    - can be seen as a form of organization, especially if you have other global styles applied to the `<html>` element\n"}function compiledContent(){return html}function getHeadings(){return[{depth:2,slug:"border-box-explained",text:"Border-Box Explained#"},{depth:3,slug:"standard-css-box-model",text:"⭐Standard CSS Box Model#"},{depth:3,slug:"alternative-css-box-model",text:"⭐Alternative CSS Box Model#"}]}const Content=createComponent(((e,n,o)=>{const{layout:s,...a}=frontmatter;return a.file=file,a.url=url,renderTemplate`${maybeRenderHead()}${unescapeHTML(html)}`}));export{Content,compiledContent,Content as default,file,frontmatter,getHeadings,rawContent,url};