import{createComponent,renderTemplate,maybeRenderHead,unescapeHTML}from"../../../../astro.mjs";import"kleur/colors";import"clsx";import"html-escaper";import"cssesc";const html='<h1 id="combinators-selector-combinations">Combinators-Selector Combinations<a class="anchor" href="#combinators-selector-combinations"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h1>\n<h3 id="combining-pseudo-classes-and-pseudo-elements">Combining pseudo-classes and pseudo-elements<a class="anchor" href="#combining-pseudo-classes-and-pseudo-elements"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>article p:first-child::first-line { }</code></li>\n</ul>\n<h3 id="descendant-combinator">‚≠ê<strong>Descendant combinator</strong><a class="anchor" href="#descendant-combinator"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>li em {color: rebeccapurple;}</code>\n<ul>\n<li>To select only an <code>&#x3C;em></code> that is nested inside an <code>&#x3C;li></code> element</li>\n</ul>\n</li>\n</ul>\n<h3 id="-child-combinator">‚≠ê <strong>Child combinator</strong><a class="anchor" href="#-child-combinator"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>article > p { }</code>\n<ul>\n<li>selects only <strong>the direct children</strong></li>\n</ul>\n</li>\n</ul>\n<h3 id="adjacent-sibling-combinator">‚≠ê<strong>Adjacent sibling combinator</strong><a class="anchor" href="#adjacent-sibling-combinator"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>h1 + p {font-size: 200%;}</code>\n<ul>\n<li>styling a paragraph when it comes directly after a heading at the same hierarchy level</li>\n</ul>\n</li>\n</ul>\n<h3 id="-general-sibling-combinator">‚≠ê <strong>General sibling combinator</strong><a class="anchor" href="#-general-sibling-combinator"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>p ~ img</code>\n<ul>\n<li>select <strong>siblings of an element even if they are not directly adjacent</strong></li>\n</ul>\n</li>\n</ul>\n<h3 id="you-can-combine-multiple-selectors-and-combinators-together">üìåyou can combine multiple selectors and combinators together<a class="anchor" href="#you-can-combine-multiple-selectors-and-combinators-together"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>article p span {}</code>\n<ul>\n<li><code>/* selects any &#x3C;span> that is inside a &#x3C;p>, which is inside an &#x3C;article> */</code></li>\n</ul>\n</li>\n<li><code>h1 + ul + p { }</code>\n<ul>\n<li><code>/* selects any &#x3C;p> that comes directly after a &#x3C;ul>, which comes directly after an &#x3C;h1> */</code></li>\n</ul>\n</li>\n</ul>\n<h3 id="-nesting-to-create-complex-selectors">üìå Nesting to create complex selectors<a class="anchor" href="#-nesting-to-create-complex-selectors"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><img src="https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/Paste-image-1695097054773image.png" alt=""></li>\n</ul>',frontmatter={aliases:["Combinators-Selector Combinations"],category:"Web Design",comment:null,draft:!1,title:"Combinators-Selector Combinations",type:"D",tags:["CSS"],DateStarted:"2024-01-09T00:00:00.000Z",DateModified:"2024-06-11T00:00:00.000Z",status:null,reviewed:1,Datereviewed:"2024-01-08T16:00:00.000Z",topic:"Selectors",difficulty:"Hard","linter-yaml-title-alias":"Combinators-Selector Combinations",minutes:1,words:120},file="E:/SynologyDrive/Codespace/blog-site/mynote/my-fuwari-blog/src/content/posts/css/css-selectors/combinators-selector-combinations.md",url=void 0;function rawContent(){return"\n# Combinators-Selector Combinations\n\n### Combining pseudo-classes and pseudo-elements\n\n- `article p:first-child::first-line { }`\n\n### ‚≠ê**Descendant combinator**\n\n- `li em {color: rebeccapurple;}`\n  - To select only an `<em>` that is nested inside an `<li>` element\n\n### ‚≠ê **Child combinator**\n\n- `article > p { }`\n  - selects only **the direct children**\n\n### ‚≠ê**Adjacent sibling combinator**\n\n- `h1 + p {font-size: 200%;}`\n  - styling a paragraph when it comes directly after a heading at the same hierarchy level\n\n### ‚≠ê **General sibling combinator**\n\n- `p ~ img`\n  - select **siblings of an element even if they are not directly adjacent**\n\n### üìåyou can combine multiple selectors and combinators together\n\n- `article p span {}`\n  - `/* selects any <span> that is inside a <p>, which is inside an <article> */`\n- `h1 + ul + p { }`\n  - `/* selects any <p> that comes directly after a <ul>, which comes directly after an <h1> */`\n\n### üìå Nesting to create complex selectors\n\n- ![](https://cdn.jsdelivr.net/gh/jenniferwonder/bimg/web-design/Paste-image-1695097054773image.png)\n"}function compiledContent(){return html}function getHeadings(){return[{depth:1,slug:"combinators-selector-combinations",text:"Combinators-Selector Combinations#"},{depth:3,slug:"combining-pseudo-classes-and-pseudo-elements",text:"Combining pseudo-classes and pseudo-elements#"},{depth:3,slug:"descendant-combinator",text:"‚≠êDescendant combinator#"},{depth:3,slug:"-child-combinator",text:"‚≠ê Child combinator#"},{depth:3,slug:"adjacent-sibling-combinator",text:"‚≠êAdjacent sibling combinator#"},{depth:3,slug:"-general-sibling-combinator",text:"‚≠ê General sibling combinator#"},{depth:3,slug:"you-can-combine-multiple-selectors-and-combinators-together",text:"üìåyou can combine multiple selectors and combinators together#"},{depth:3,slug:"-nesting-to-create-complex-selectors",text:"üìå Nesting to create complex selectors#"}]}const Content=createComponent(((e,n,t)=>{const{layout:o,...a}=frontmatter;return a.file=file,a.url=url,renderTemplate`${maybeRenderHead()}${unescapeHTML(html)}`}));export{Content,compiledContent,Content as default,file,frontmatter,getHeadings,rawContent,url};