import{createComponent,renderTemplate,maybeRenderHead,unescapeHTML}from"../../../../chunks/1726300354279/astro.mjs";import"kleur/colors";import"clsx";import"html-escaper";import"cssesc";const id="css/css-selectors/basic-selectors.md",collection="posts",slug="css/css-selectors/basic-selectors",body="\n# Basic Selectors\n\n## Selector List\n\n### combine these into a selector list, by adding a **comma** between them\n\n### `h1, .special { color: blue;}`\n\n## **Universal Selector**\n\n### `*`\n\n### reset stylesheets\n\n- to remove the margins on all elements\n- strip out all of the browser styling\n\n### make your selectors easier to read, making it more obvious what the selector is doing\n\n- `article *:first-child`{ }\n\n### Global Style (全局样式设置)\n\n## **Element Selector** (Type/ Tag name selector)\n\n### `p, li, h1 { }`\n\n### `body`\n\n### `html`\n\n## **ID Selector**\n\n### `#onething`\n\n### can be used only **once per page**, and elements can only have **a single id value** applied to them\n\n### It can select an element that has the id set on it\n\n### To only target the element without applying the block rules, precede the ID with a Type Selector\n\n- `h1#heading {color: purple;}`\n  - ‼️ The color will not be set to purple\n\n### an ID has high specificity. It will overrule most other selectors.\n\n### In most cases, it is preferable to add a **class** to an element instead of an ID\n\n## **Class Selector**\n\n### `.className{ }`\n\n### select a subset of the elements without changing the others\n\n### Targeting classes on particular elements\n\n- `h1.highlight {background-color: pink;}`\n- The rule will only apply to that particular element and class combination\n\n### Target an element if it has more than one class applied\n\n- `.notebox.warning {border-color: orange; font-weight: bold;}`\n",data={title:"Basic Selectors",DateStarted:new Date(17047584e5),draft:!1,tags:["CSS"],category:"Web Design"},_internal={type:"content",filePath:"E:/SynologyDrive/Codespace/blog-site/mynote/my-fuwari-blog/src/content/posts/css/css-selectors/basic-selectors.md",rawData:void 0},html='<h1 id="basic-selectors">Basic Selectors<a class="anchor" href="#basic-selectors"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h1>\n<h2 id="selector-list">Selector List<a class="anchor" href="#selector-list"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="combine-these-into-a-selector-list-by-adding-a-comma-between-them">combine these into a selector list, by adding a <strong>comma</strong> between them<a class="anchor" href="#combine-these-into-a-selector-list-by-adding-a-comma-between-them"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="h1-special--color-blue"><code>h1, .special { color: blue;}</code><a class="anchor" href="#h1-special--color-blue"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="universal-selector"><strong>Universal Selector</strong><a class="anchor" href="#universal-selector"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id=""><code>*</code></h3>\n<h3 id="reset-stylesheets">reset stylesheets<a class="anchor" href="#reset-stylesheets"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li>to remove the margins on all elements</li>\n<li>strip out all of the browser styling</li>\n</ul>\n<h3 id="make-your-selectors-easier-to-read-making-it-more-obvious-what-the-selector-is-doing">make your selectors easier to read, making it more obvious what the selector is doing<a class="anchor" href="#make-your-selectors-easier-to-read-making-it-more-obvious-what-the-selector-is-doing"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>article *:first-child</code>{ }</li>\n</ul>\n<h3 id="global-style-全局样式设置">Global Style (全局样式设置)<a class="anchor" href="#global-style-全局样式设置"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="element-selector-type-tag-name-selector"><strong>Element Selector</strong> (Type/ Tag name selector)<a class="anchor" href="#element-selector-type-tag-name-selector"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="p-li-h1--"><code>p, li, h1 { }</code><a class="anchor" href="#p-li-h1--"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="body"><code>body</code><a class="anchor" href="#body"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="html"><code>html</code><a class="anchor" href="#html"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="id-selector"><strong>ID Selector</strong><a class="anchor" href="#id-selector"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="onething"><code>#onething</code><a class="anchor" href="#onething"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="can-be-used-only-once-per-page-and-elements-can-only-have-a-single-id-value-applied-to-them">can be used only <strong>once per page</strong>, and elements can only have <strong>a single id value</strong> applied to them<a class="anchor" href="#can-be-used-only-once-per-page-and-elements-can-only-have-a-single-id-value-applied-to-them"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="it-can-select-an-element-that-has-the-id-set-on-it">It can select an element that has the id set on it<a class="anchor" href="#it-can-select-an-element-that-has-the-id-set-on-it"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="to-only-target-the-element-without-applying-the-block-rules-precede-the-id-with-a-type-selector">To only target the element without applying the block rules, precede the ID with a Type Selector<a class="anchor" href="#to-only-target-the-element-without-applying-the-block-rules-precede-the-id-with-a-type-selector"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>h1#heading {color: purple;}</code>\n<ul>\n<li>‼️ The color will not be set to purple</li>\n</ul>\n</li>\n</ul>\n<h3 id="an-id-has-high-specificity-it-will-overrule-most-other-selectors">an ID has high specificity. It will overrule most other selectors.<a class="anchor" href="#an-id-has-high-specificity-it-will-overrule-most-other-selectors"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="in-most-cases-it-is-preferable-to-add-a-class-to-an-element-instead-of-an-id">In most cases, it is preferable to add a <strong>class</strong> to an element instead of an ID<a class="anchor" href="#in-most-cases-it-is-preferable-to-add-a-class-to-an-element-instead-of-an-id"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h2 id="class-selector"><strong>Class Selector</strong><a class="anchor" href="#class-selector"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h2>\n<h3 id="classname-"><code>.className{ }</code><a class="anchor" href="#classname-"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="select-a-subset-of-the-elements-without-changing-the-others">select a subset of the elements without changing the others<a class="anchor" href="#select-a-subset-of-the-elements-without-changing-the-others"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<h3 id="targeting-classes-on-particular-elements">Targeting classes on particular elements<a class="anchor" href="#targeting-classes-on-particular-elements"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>h1.highlight {background-color: pink;}</code></li>\n<li>The rule will only apply to that particular element and class combination</li>\n</ul>\n<h3 id="target-an-element-if-it-has-more-than-one-class-applied">Target an element if it has more than one class applied<a class="anchor" href="#target-an-element-if-it-has-more-than-one-class-applied"><span class="anchor-icon" data-pagefind-ignore="">#</span></a></h3>\n<ul>\n<li><code>.notebox.warning {border-color: orange; font-weight: bold;}</code></li>\n</ul>',frontmatter={category:"Web Design",comment:null,aliases:["Basic Selectors"],draft:!1,title:"Basic Selectors",type:"D",tags:["CSS"],DateStarted:"2024-01-09T00:00:00.000Z",DateModified:"2024-04-19T00:00:00.000Z",status:null,reviewed:1,Datereviewed:"2024-01-08T16:00:00.000Z",topic:"Selectors",difficulty:"Good","linter-yaml-title-alias":"Basic Selectors",minutes:1,words:190},file="E:/SynologyDrive/Codespace/blog-site/mynote/my-fuwari-blog/src/content/posts/css/css-selectors/basic-selectors.md",url=void 0;function rawContent(){return"\n# Basic Selectors\n\n## Selector List\n\n### combine these into a selector list, by adding a **comma** between them\n\n### `h1, .special { color: blue;}`\n\n## **Universal Selector**\n\n### `*`\n\n### reset stylesheets\n\n- to remove the margins on all elements\n- strip out all of the browser styling\n\n### make your selectors easier to read, making it more obvious what the selector is doing\n\n- `article *:first-child`{ }\n\n### Global Style (全局样式设置)\n\n## **Element Selector** (Type/ Tag name selector)\n\n### `p, li, h1 { }`\n\n### `body`\n\n### `html`\n\n## **ID Selector**\n\n### `#onething`\n\n### can be used only **once per page**, and elements can only have **a single id value** applied to them\n\n### It can select an element that has the id set on it\n\n### To only target the element without applying the block rules, precede the ID with a Type Selector\n\n- `h1#heading {color: purple;}`\n  - ‼️ The color will not be set to purple\n\n### an ID has high specificity. It will overrule most other selectors.\n\n### In most cases, it is preferable to add a **class** to an element instead of an ID\n\n## **Class Selector**\n\n### `.className{ }`\n\n### select a subset of the elements without changing the others\n\n### Targeting classes on particular elements\n\n- `h1.highlight {background-color: pink;}`\n- The rule will only apply to that particular element and class combination\n\n### Target an element if it has more than one class applied\n\n- `.notebox.warning {border-color: orange; font-weight: bold;}`\n"}function compiledContent(){return html}function getHeadings(){return[{depth:1,slug:"basic-selectors",text:"Basic Selectors#"},{depth:2,slug:"selector-list",text:"Selector List#"},{depth:3,slug:"combine-these-into-a-selector-list-by-adding-a-comma-between-them",text:"combine these into a selector list, by adding a comma between them#"},{depth:3,slug:"h1-special--color-blue",text:"h1, .special { color: blue;}#"},{depth:2,slug:"universal-selector",text:"Universal Selector#"},{depth:3,slug:"",text:"*"},{depth:3,slug:"reset-stylesheets",text:"reset stylesheets#"},{depth:3,slug:"make-your-selectors-easier-to-read-making-it-more-obvious-what-the-selector-is-doing",text:"make your selectors easier to read, making it more obvious what the selector is doing#"},{depth:3,slug:"global-style-全局样式设置",text:"Global Style (全局样式设置)#"},{depth:2,slug:"element-selector-type-tag-name-selector",text:"Element Selector (Type/ Tag name selector)#"},{depth:3,slug:"p-li-h1--",text:"p, li, h1 { }#"},{depth:3,slug:"body",text:"body#"},{depth:3,slug:"html",text:"html#"},{depth:2,slug:"id-selector",text:"ID Selector#"},{depth:3,slug:"onething",text:"#onething#"},{depth:3,slug:"can-be-used-only-once-per-page-and-elements-can-only-have-a-single-id-value-applied-to-them",text:"can be used only once per page, and elements can only have a single id value applied to them#"},{depth:3,slug:"it-can-select-an-element-that-has-the-id-set-on-it",text:"It can select an element that has the id set on it#"},{depth:3,slug:"to-only-target-the-element-without-applying-the-block-rules-precede-the-id-with-a-type-selector",text:"To only target the element without applying the block rules, precede the ID with a Type Selector#"},{depth:3,slug:"an-id-has-high-specificity-it-will-overrule-most-other-selectors",text:"an ID has high specificity. It will overrule most other selectors.#"},{depth:3,slug:"in-most-cases-it-is-preferable-to-add-a-class-to-an-element-instead-of-an-id",text:"In most cases, it is preferable to add a class to an element instead of an ID#"},{depth:2,slug:"class-selector",text:"Class Selector#"},{depth:3,slug:"classname-",text:".className{ }#"},{depth:3,slug:"select-a-subset-of-the-elements-without-changing-the-others",text:"select a subset of the elements without changing the others#"},{depth:3,slug:"targeting-classes-on-particular-elements",text:"Targeting classes on particular elements#"},{depth:3,slug:"target-an-element-if-it-has-more-than-one-class-applied",text:"Target an element if it has more than one class applied#"}]}const Content=createComponent(((e,t,n)=>{const{layout:a,...s}=frontmatter;return s.file=file,s.url=url,renderTemplate`${maybeRenderHead()}${unescapeHTML(html)}`})),basicSelectors=Object.freeze(Object.defineProperty({__proto__:null,Content:Content,compiledContent:compiledContent,default:Content,file:file,frontmatter:frontmatter,getHeadings:getHeadings,rawContent:rawContent,url:url},Symbol.toStringTag,{value:"Module"}));export{_internal,basicSelectors,body,collection,data,id,slug};